import { Router } from "express";
import ScoreCard from "../models/ScoreCard";
const router = Router();

router.delete("/cards", async (_, res) => {
    try{
        await ScoreCard.deleteMany({});
        console.log("Database deleted");
        res.json({message: 'Database Cleared'})
    } catch (e) { throw new Error("Database deletion failed"); }
}); //Header (Delete)
// bug: didn't clear

router.post("/card", async (req, res) => {
    try{
        const newScoreCard = {name:req.body.name, subject:req.body.subject, score:req.body.score};
        const record = await ScoreCard.updateOne({ name:req.body.name, subject:req.body.subject }, {
            $set: newScoreCard
        }, { upsert: true });
        console.log(record);
        const updated = record.upsertedId === null;
        const s = (updated ? 'Updating ' : 'Adding ') + newScoreCard.name + ' ' + newScoreCard.subject + ' ' + newScoreCard.score; 
        res.json({
            message: s,
            card: newScoreCard
        });
    } catch(e) { throw new Error("ScoreCard creation error: " + e); }
    // {
    //     try{
    //         const newScoreCard = new ScoreCard({name:req.body.name, subject:req.body.subject, score:req.body.score});
    //         console.log("Create ScoreCard", newScoreCard);
