                    status: 2
                });
                return;*/
                type = 2;
            }
        }
    }
    //console.log(size);
    //console.log(board);

    if(type === 0)
    {
        res.json({
            message: 'keep going...',
            status: 0,
        });
    }
    else if(type === 1)
    {
        res.json({
            message: 'You win! You earn 50 coins!',
            status: 1,
        });
    }
    else if(type === 2)
    {
        res.json({
            message: 'You lose! You earn 10 coins!',
            status: 2,
        });
    }
    else if(type === 3)
    {
        res.json({
            message: 'Tie game. You earn 20 coins!',
            status: 3,
        });
    }
});

